import { configureStore, createSlice } from "@reduxjs/toolkit";

const initialState = { Authenticated: "", Authorization: "" };

const headerSlice = createSlice({
  name: "headers",
  initialState,
  reducers: {
    setHeaderOnLogin(state, action) {
      initialState.Authenticated = action.payload.Authenticated;
      initialState.Authorization = action.payload.Authorization;
    },
    setHeaderOnLogout(state) {
      (initialState.Authenticated = ""), (initialState.Authorization = "");
    },
  },
});

const store = configureStore({
  reducer: headerSlice.reducer,
});

export const headersAction = headerSlice.actions;
export default store;
