import classes from "./PostItem.module.css";

const PostItem = (props) => {
  return (
    <li key={props.object.id} className={classes["post-container"]}>
      <h1 className={classes.heading}>{props.object.title}</h1>
      <p>{props.object.description}</p>
      <div className={classes.info}>
        <h4>Written by: {props.object.user}</h4>
      </div>
    </li>
  );
};

export default PostItem;
