import ProfileForm from "./ProfileForm";
import classes from "./UserProfile.module.css";
import { useSelector } from "react-redux";
const UserProfile = () => {
  const authenticatedSelector = useSelector((state) => state.Authenticated);
  const authorizationSelector = useSelector((state) => state.Authorization);

  if (isLogin) {
    let url = "http://localhost:8080/login";
    fetch(url, {
      method: "POST",
      body: JSON.stringify({
        username: username,
        password: password,
      }),
      headers: { "Content-Type": "application/json" },
    }).then(async (res) => {
      if (res.ok) {
        res.json().then((data) => {
          const tokenHeader = data.Authorization;
          const userHeader = data.Authenticated;
          dispatch(
            headersAction.setHeaderOnLogin({
              Authenticated: userHeader,
              Authorization: tokenHeader,
            })
          );
        });
      } else {
        console.log("Problema con il login");
      }
    });
  }

  return (
    <section className={classes.profile}>
      <h1>Your User Profile</h1>
      <p>Authenticated {authenticatedSelector}</p>
      <p>Authorization {authorizationSelector}</p>
      <ProfileForm />
    </section>
  );
};

export default UserProfile;
